def show_bartender_bible_categories():
    """–ü–æ–∫–∞–∑–∞—Ç—å –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ —Ä–µ—Ü–µ–ø—Ç–æ–≤ –∏–∑ –ë–∏–±–ª–∏–∏ –±–∞—Ä–º–µ–Ω–∞"""
    conn = sqlite3.connect('cocktails.db')
    cursor = conn.cursor()
    
    cursor.execute("SELECT DISTINCT category FROM recipes WHERE source = '–ë–∏–±–ª–∏—è –±–∞—Ä–º–µ–Ω–∞' ORDER BY category")
    categories = cursor.fetchall()
    
    print("\nüìã –ö–∞—Ç–µ–≥–æ—Ä–∏–∏ —Ä–µ—Ü–µ–ø—Ç–æ–≤ –∏–∑ '–ë–∏–±–ª–∏—è –±–∞—Ä–º–µ–Ω–∞':")
    for category in categories:
        print(f"  ‚Ä¢ {category[0]}")
    
    conn.close()

